mapper.select:
select 
from (((orderform o INNER JOIN user u ON o.uid=u.openid)INNER JOIN collector c ON o.cid=c.id)
INNER JOIN scrapmessage sc ON o.id=sc.oid)INNER JOIN useraddress  a ON o.aid=a.id
where
   o.id=#{id}

select 
from (((orderform o INNER JOIN user u ON o.uid=u.openid)INNER JOIN collector c ON o.cid=c.id)
INNER JOIN scrapmessage sc ON o.id=sc.oid)INNER JOIN useraddress  a ON o.aid=a.id
where
   o.cid=#{cid}
and  o.state=#{state}
order BY o.id
limit #{offset},#{limit}

select 
from (((orderform o INNER JOIN user u ON o.uid=u.openid)INNER JOIN collector c ON o.cid=c.id)
INNER JOIN scrapmessage sc ON o.id=sc.oid)INNER JOIN useraddress  a ON o.aid=a.id
where
  o.uid=#{uid}
and  o.state=#{state}
order BY o.id
limit #{offset},#{limit}

select 
from (((orderform o INNER JOIN user u ON o.uid=u.openid)INNER JOIN collector c ON o.cid=c.id)
INNER JOIN scrapmessage sc ON o.id=sc.oid)INNER JOIN useraddress  a ON o.aid=a.id
where
   o.state=#{state}
order BY o.id
limit #{offset},#{limit}

 select o.state,o.weight,o.createTime,o.appointTime,o.updateTime,o.price,o.aid,o.uid,o.cid,d.uid,d.address,d.detail,d.community_id,d.geohash,X(position) as X(d.position),Y(position) as Y(d.position)

 from orderform o
 INNER JOIN useraddress d
 where
 Math.sqrt(toRadians(X(position) -#{locationX}) * 6367000.0* Math.cos(toRadians((Y(position) +#{locationY})/2.0)) * toRadians(X(position) -#{locationX}) * 6367000.0* Math.cos(toRadians((Y(position) +#{locationY})/2.0)) + 6367000.0 * toRadians(Y(position) -#{locationY}) * 6367000.0 * toRadians(Y(position) -#{locationY}))   <= #{distance}

and d.geohash LIKE CONCAT(?, '%');
 order BY o.id
 limit #{offset},#{limit}
